// n8n Code node: Filter New (and optionally relevance later)

// 1) Pull the feed cursor (Last Item Published At) from the current feed record
// Depending on node output, it may be at $json.fields['Last Item Published At'] or $json['Last Item Published At']
const feed = $('Loop Each Feed').first().json;
const cursorRaw =
  feed?.fields?.['Last Item Published At'] ??
  feed?.['Last Item Published At'] ??
  null;

// Airtable Date field usually comes through as ISO string. If empty, treat as null.
const cursor = cursorRaw ? new Date(cursorRaw) : null;

// 2) Helper to parse RSS pubDate variants
function parsePubDate(itemJson) {
  const raw =
    itemJson.pubDate ??
    itemJson.isoDate ??
    itemJson.published ??
    itemJson.date ??
    null;

  if (!raw) return null;

  const d = new Date(raw);
  return isNaN(d.getTime()) ? null : d;
}

// 3) Filter: if no cursor, treat all as new. Else only keep pubDate > cursor.
const out = [];

for (const item of $input.all()) {
  const pub = parsePubDate(item.json);

  // If item has no pub date, you have two options:
  // Option 1 (safe): treat as NOT new and drop it
  // Option 2 (permissive): treat as new and keep it
  // I'll choose safe by default:
  if (!pub) continue;

  const isNew = !cursor || pub > cursor;

  if (isNew) out.push(item);
}

return out;

